
create or replace function update_games_comments()
return trigger as $$
begin
    update games set
    commentsCount = c.commentsCount,
    commentsRate = c.commentsRate
    from (select gameId, count(rate) as commentsCount, avg(rate) as commentsRate from comments where gameId=new.gameId group by gameId) as c
    where c.gameId = game.id;
    return new;
end;

create trigger trigger_update_games_comment
after update for each row
execute procedure update_games_comments();







